package printingstatements;

public class DayExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day = 3;
		switch (day) {
		case 1:
			System.out.println("Today is Monday");
			System.out.println("Have an awesome day");
			break;
		case 2:
			System.out.println("Today is Tuesday");
			System.out.println("Have an awesome day");
			break;
		case 3:
			System.out.println("Today is Wednesday");
			System.out.println("Have an awesome day");
			System.out.println("It's almost the weekend");
			break;
		case 4:
			System.out.println("Today is Thursday");
			System.out.println("Have an awesome day");
			break;
		case 5:
			System.out.println("Today is Friday");
			System.out.println("Have an awesome day");
			System.out.println("It's the weekend baby");
			break;
		case 6:
			System.out.println("Today is Saturday");
			System.out.println("Have an awesome day");
			break;
		case 7:
			System.out.println("Today is Sunday");
			System.out.println("Have an awesome day");
			break;
		default:
			System.out.println("Invalid day");
			System.out.println("Please select your day");
		}

	}

}
